const imageBase64Data = ``;

const createDrawing = (drawingOptions?: IDrawingOptions): Drawing =>
    new Drawing(
        {
            type: "jpg",
            fileName: "test.jpg",
            data: Buffer.from(imageBase64Data, "base64"),
            transformation: {
                pixels: {
                    x: 100,
                    y: 100,
                },
                emus: {
                    x: 100 * 9525,
                    y: 100 * 9525,
                },
            },
        },
        drawingOptions,
    );

